package hudson.plugins.promoted_builds.integrations.jobdsl;

import groovy.util.Node;
import hudson.plugins.promoted_builds.conditions.ManualCondition;

import java.util.Collection;

import com.thoughtworks.xstream.annotations.XStreamAlias;

/**
 * Special holder for the ManualCondition generated by the Job DSL Plugin
 * 
 * @author Dennis Schulte
 * @see ManualConditionConverter
 * @deprecated Should be avoided in favor of Automatically Generated DSL. Relying on name collision does not scale
 *  well.
 */
@XStreamAlias("hudson.plugins.promoted_builds.conditions.ManualCondition")
@Deprecated
public class JobDslManualCondition extends ManualCondition {

    private Collection<Node> parameterDefinitionNodes;

    public Collection<Node> getParameterDefinitionNodes() {
        return parameterDefinitionNodes;
    }

    public void setParameterDefinitionNodes(Collection<Node> parameterDefinitionNodes) {
        this.parameterDefinitionNodes = parameterDefinitionNodes;
    }

}
